(3,	'{\"en\":\"Analytics\"}',	'analytics',	'{\"en\":\"<p>Analytical cookies are used to understand how visitors interact with the website. These cookies help provide information on metrics such as the number of visitors, bounce rate, traffic source, etc.<\\/p>\"}',	0,	1,	0,	1,	'[]',	'0000-00-00 00:00:00',	'2023-03-16 14:28:43'),
(4,	'{\"en\":\"Performance\"}',	'performance',	'{\"en\":\"<p>Performance cookies are used to understand and analyze the key performance indexes of the website which helps in delivering a better user experience for the visitors.<\\/p>\"}',	0,	1,	0,	1,	'[]',	'0000-00-00 00:00:00',	'2023-03-16 14:28:43'),
(5,	'{\"en\":\"Advertisement\"}',	'advertisement',	'{\"en\":\"<p>Advertisement cookies are used to provide visitors with customized advertisements based on the pages you visited previously and to analyze the effectiveness of the ad campaigns.<\\/p>\"}',	0,	1,	0,	1,	'[]',	'0000-00-00 00:00:00',	'2023-03-16 14:28:43');
